# Checa se existe o termo na lista
# Parâmetros: term - string
# Retorno: boolean
def check_term(term):
    return term in terms_dict

# Obtem o nome do termo
# Parâmetros: term - string
# Retorno: string
def get_term(term):
    return terms_dict.get(term, 'Termo não encontrado')

# Remove um termo
# Parâmetros: term - string
def del_term(term):
    del terms_dict[term]

# Adiciona um termo
# Parâmetros: term - string, description -string
def add_term(term, description):
    terms_dict[term] = description